/* ==UserStyle==
@name           Cpp Reference Dark
@namespace      USO Archive
@author         abhishekchakraborty
@description    `Dark Theme for <a href="http://en.cppreference.com">C++ Reference</a>`
@version        20170901.18.45
@license        CC-BY-4.0
@preprocessor   uso
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cppreference.com"), url-prefix("file:///usr/share/cppreference/") {

  :root {
    --default-bkgd-color: #333333;
    --darker-bkgd-color: #282828;
    --lighter-bkgd-color: #404040;
    --table-bkgd-color: #474747;
    --border-color: #666;
    --box-color: #777;
    --default-color: #d1d1d1;
  }

  /* Contents Page */
  .mainpagetable tr.row td {
    background-color: var(--default-bkgd-color); /* #3f3f3f; */
  }

  /* for below Footer */
  body {
    background-color: var(--default-bkgd-color);
  }

  /* Page Background */
  div#cpp-content-base {
    background-color: var(--default-bkgd-color);
  }

  /* Page text */
  div#content {
    color: var(--default-color);
    background-color: var(--default-bkgd-color); /* #3f3f3f; */
  }

  /* Boxed text */
  .t-rev-inl {
    border-color: var(--box-color);
  }

  .t-rev > td:nth-child(1), .t-rev > td:nth-child(2) {
    border-color: var(--box-color);
  }

  /* Link */
  a {
    color: #5291f8;
  }

  a:visited {
    color: #6ba1f9;
  }

  a.new {
    color: #ff4444;
  }

  /* Top Pane */
  div#cpp-head-first-base {
    background-color:var(--default-bkgd-color);
    border-bottom-color: #999;
  }

  /* "cppReference.com" */
  div#cpp-head-first > h5 > a {
    color: #a7a7a7;
  }

  /* User dropdown */
  div#cpp-head-personal ul {
    background-color: var(--default-bkgd-color);
  }

  /* Search input */
  div#simpleSearch {
    background-color: var(--lighter-bkgd-color);
    background-image: unset;
  }

  div#simpleSearch input#searchInput {
    color: var(--default-color);
  }

  div#simpleSearch button#searchButton img {
    filter: invert(100%);
  }

  /* Search dropdown */
  .suggestions-special {
    color: #929292;
    background-color: #3d3d3d;
  }

  .suggestions-special .special-query {
    color: #b8b8b8;
  }

  .suggestions-result-current .special-label, .suggestions-result-current .special-query {
    color: #a7a7a7;
  }

  /* Search results */
  table.mw-cppsearch-groups {
    background-color: var(--darker-bkgd-color);
  }

  /* Middle Pane */
  div#cpp-head-second-base {
    background-color: var(--default-bkgd-color);
    background-image: unset;
    border-bottom: 1px ridge #aaa;
  }

  div#p-namespaces.vectorTabs {
    background-image: unset;
  }

  div.vectorTabs span {
    /* background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAuCAYAAADp73NqAAAAmklEQVQImXXEMQtBUQAF4POfblEGvdQrSi9JKYoURZFBZ2B4WSSlyKAoJUU2i7pWg8Vg9VvedCz3jr7hgzFG+FsqTVcm8GVDCkFohFzBCGFkhHxEoVimUKr7Kk0K1TaFWpdCY0ChRQqdCYVeTKEfUxjOKYyWFMZr33RnXbOTFRYXK6xuvs3dCtuHFfZP3+FlheM7cZ0/vus30Q+O/G5ErVPgOQAAAABJRU5ErkJggg==);   / * Alternate image inverted from original * /    */
    background-image: unset;
    background-color: var(--default-bkgd-color);
  }

  div#p-views.vectorTabs {
    background-image: unset;
  }

  div.vectorTabs ul li a {
    color: var(--default-color);
  }

  /* div.vectorTabs ul li.selected{
    background-image: none;
  } */

  div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
    color: var(--default-color);
  }

  div.vectorMenu {
    background-image: none;
  }

  div.vectorMenu h5 {
    background-image: none;
  }

  div.vectorMenu li a {
    color: #5291f8;
    background-color: var(--default-bkgd-color);
  }

  /* API declarations */
  .t-dcl > td, 
  .t-dcl-h > td, 
  .t-dcl-sep > td, 
  .t-dcl-rev > .t-dcl:first-child > td, 
  .t-dcl-rev > .t-dcl-rev-aux > td, 
  .t-dcl-rev > .t-dcl > td, 
  .t-dcl-rev > .t-dcl-h > td {
    border-top-color: var(--border-color);
  }

  /* API surrounding boxes */
  .t-c {
    border-color: var(--border-color);
  }

  /* 'Operations', 'Invalidated' table */
  .dsctable > tbody > tr > td {
    border-top-color: var(--border-color);
  }

  .dsctable > tbody > tr:last-child > td {
    border-bottom-color: var(--border-color);
  }

  /* 'Member types', 'Member functions', 'See Also' */
  .t-dsc > td {
    border-top-color: var(--border-color);
  }

/*   / * underlines 'Member types' * /
  .t-dsc-hitem > td {
    border-top-color = #cccccc by default
  } */

  /* underlines 'Member functions' */
  .t-dsc-begin > tbody > .t-dsc:first-child > td {
    border-top-color: #cccccc;
  }

/*   / * underlines the sub-headers * /
  .t-dsc-begin > tbody > tr:not(.t-dsc) + tr > td {
    border-top-color: #cccccc;
  } */

  /* separators in 'Initialization' in C++ Language */
  .t-sdsc-sep {
    border-top-color: var(--border-color);
  }

  /* Footer */
  div#cpp-footer-base {
    background-color: var(--default-bkgd-color);
  }

  div#footer ul li {
    color: var(--default-color);
  }

  h1, h2, h3, h4, h5, h6 {
    color: var(--default-color);
  }

  /* template, conditional, loop keywords */
  .cpp.source-cpp .kw1 {
    color: #4fa2b3;
  }

  /* bool */
  .cpp.source-cpp .kw2 {
    color: #84c103;
  }

  /* keyword */
  .cpp.source-cpp .kw4 {
    color: #ac7339;
  }

  /* numeric literal 1 */
  .cpp.source-cpp .nu0 {
    color: #7aa3c3;
  }

  .cpp.source-cpp .nu19, .cpp.source-cpp .nu16 {
    color: #6d99c8;
  }

  /* comparison & shift operators */
  .cpp.source-cpp .sy1 {
    color: #4b7ea5;
  }

  /* indirection, increment, decrement operators */
  .cpp.source-cpp .sy2 {
    color: #4fa2b3;
  }

  /* not operator */
  .cpp.source-cpp .sy3 {
    color: #4fa2b3;
  }

  .mw-geshi a {
    color: #4fa2b3;
  }

  pre {
    color: var(--default-color);
  }

  pre, div.mw-geshi {
    background-color: var(--darker-bkgd-color);
  }

  /* "C++" dropdown */
  .t-navbar-menu > div {
    background-color: var(--default-bkgd-color);
  }

  /**** Table *****/
  table.wikitable {
    color: var(--default-color);
    background-color: var(--darker-bkgd-color);
  }

  table.wikitable > * > tr > td {
    background-color: var(--default-bkgd-color) !important;
  }

  table.wikitable > tr > th, table.wikitable > * > tr > th {
    background-color: var(--table-bkgd-color);
  }

  /* green, red elements e.g. http://en.cppreference.com/w/cpp/compiler_support */
  table.wikitable > * > tr > td.table-yes { /* Green */
    background-color: #245224 !important; /* from #3b873b */
    color: #eee !important;
  }

  table.wikitable > * > tr > td.table-no { /* Red */
    background-color: #7f2e2e !important; /* from #ff9090 -> #ff5d5d */
    color: #eee !important;
  }

  /* blank cells */
  .table-na {
    background-color: var(--table-bkgd-color) !important;
  }

  /* green, purple, yellow elements e.g. http://en.cppreference.com/w/cpp/container */
  tr[style="background: #bcbcff;"], td[style="width:4em; background:#bcbcff;"] { /* C++03 */
    background-color: #5959cc !important;
  }

  tr[style="background: #bcffbc;"], td[style="width:4em; background:#bcffbc;"] { /* C++11 */
    background-color: #3b873b !important;
  }

  tr[style="background: #ffeebc;"], td[style="width:4em; background:#ffeebc;"] { /* C++17 */
    background-color: #a47a00 !important;
  }

  span[style="font-size:0.9em; color: #505050;"] {
    color: var(--default-color) !important;
  }

  /* green, purple header rows */
  tr[style="background: #b0b0d0;"] {
    background-color: #565690 !important;
    color: #eee;
  }

  tr[style="background: #b0d0b0;"] {
    background-color: #337655 !important;
    color: #eee;
  }

  /* Message e.g. Section Incomplete */
  table.ambox {
    background-color: var(--darker-bkgd-color);
  }

  /** Members table e.g. std::bind **/ 
  div.t-member {
    background-color: #27292b;
  }

  /* Discussions */
  element {
    background-color: #4c4733;
  }

  /** User account Preferences **/
  #preferences {
    background-color: var(--default-bkgd-color);
    background-image: none;
  }

  #preferences legend {
    color: #dadada;
  }

  #preftoc a {
    color: #0963f6;
  }

  /* Watch/Unwatch notifications */
  .mw-notification {
    background-color: var(--lighter-bkgd-color);
    border-color: var(--border-color);
  }

  /**** Inheritance Diagram ******/
  div.t-inheritance-diagram > div.center > div.noresize > img {
    filter: invert(80%);
  }

  /* Members - std::is_trivial */
  .t-inherited {
    color: var(--default-color);
    background-color: var(--darker-bkgd-color);
  }

  /** Site-specific **/
  div.contents {
    background-color: var(--darker-bkgd-color);
  }

  /** For CppCon message **/
  table.plainlinks.fmbox.fmbox-system.noprint {
    background-color: var(--default-bkgd-color) !important;
  }

  table.fmbox-system {
    background-color: var(--darker-bkgd-color);
  }

  /*** Discussion Page ***/

  /* Archives box */
  table.tmbox {
    background-color: var(--darker-bkgd-color);
    border-color: var(--border-color);
  }

  /* individual archive msg. box */
  table.plainlinks.fmbox.fmbox-system {
    background-color: var(--darker-bkgd-color) !important;
  }

  /*** History Page ***/

  #pagehistory li {
    border-color: var(--border-color);
  }

  #pagehistory li.selected {
    background-color: var(--darker-bkgd-color);
  }

  .mw-plusminus-neg {
    color: #e81414; /* from default #8b0000 */
  }

  .mw-plusminus-pos {
    color: #009700; /* from default #006400 */
  }

  /** Compare Revisions **/
  table.diff {
    background-color: var(--default-bkgd-color);
  }

  td.diff-context {
    background-color: var(--darker-bkgd-color);
    border-color: var(--border-color);
    color: var(--default-color);
  }

  .diffchange {
    color: #bbb;
  }

  table.diff td.diff-marker {
    color: var(--border-color);
  }

  /* removed */
  td.diff-deletedline .diffchange {
    background-color: #7a5602;
  }

  td.diff-deletedline {
    border-color: #b68400;
  }

  /* added */
  td.diff-addedline .diffchange {
    background-color: #003b74;
  }

  td.diff-addedline {
    border-color: #0055a5;
  }

  /**** other Diagrams ******/

  /* std::basic_streambuf */
  img[alt="std-streambuf.svg"] {
    filter: invert(70%);
  }

  /**** Technical Specfications -> Extensions ( having [Expand] button ) ******/
  th[style="background: #F0F2F5; font-size:87%; padding:0.2em 0.3em; text-align:center;"] {
    background-color: var(--darker-bkgd-color) !important;
  }

  td[style="border: solid 1px silver; padding: 8px; background: white;"] {
    background-color: var(--default-bkgd-color) !important;
  }

  /* 'Members and non-members identical to...' pane e.g. std::experimental::shared_ptr */
  div[style="overflow: hidden; margin: 1em 0 0 0; border: 1px solid #aaa; background-color: #e3f9df; padding: 0.2em 1em 1em 1em;"] {
    background-color: #27292b !important;
  }

  /**** std Symbol Index ******/
  .toccolours {
    background-color: var(--darker-bkgd-color);
    border-color: var(--border-color);
  }

  /**** Template documentation e.g. en.cppreference.com/w/Template:trim ******/

  table.ombox {
    background-color: var(--darker-bkgd-color);
    border-color: var(--border-color);
  }

  .template-documentation {
    background-color: #27292b;
  }

  /****  Coliru online compiler Editor ******/
  .ace-tm {
    background-color: var(--darker-bkgd-color);
    color: #ccc;
  }

  /* 'Run', 'Share', 'Exit' */
  .coliru-btn {
    background-color: var(--default-bkgd-color);
    background-image: none;
    color: #aaaaaa;
  }

  .coliru-btn:hover {
    background-color: var(--border-color);
    background-image: none;
    color: #ccc;
  }

  /* Compiler Selector */
  .coliru-select-compiler select {
    background-color: var(--default-bkgd-color);
    background-image: none;
    color: #aaaaaa;
  }

  /* blinking cursor */
  .ace-tm .ace_cursor {
    color: #aaaaaa;
  }

  /* vertical margin, on middle-right across editor */
  .ace-tm .ace_print-margin {
    background-color: var(--default-bkgd-color);
  }

  /* active line row */
  .ace-tm .ace_gutter-active-line {
    background-color: #a1a1a1;
  }

  /* code folds */
  .ace-tm .ace_fold {
    background-color: black;
  }

  /* selected word */
  .ace-tm .ace_marker-layer .ace_selection {
    background: rgba(192, 192, 192, 0.15);
  }

  .ace-tm .ace_marker-layer .ace_active-line {
    background-color: rgba(0, 0, 0, 0.15);
  }

  /* Error symbol */
  .ace_gutter-cell.ace_error {
  }

  /* Line No.s */
  .ace-tm .ace_gutter {
    background-color: var(--default-bkgd-color);
    color: var(--border-color);
  }

  /* Block Indent vertical line */
  .ace-tm .ace_indent-guide {
    background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAOxAAADsQBlSsOGwAAABJJREFUCJlj+P///38GBgaG/wAe6wT8qpLcqwAAAABJRU5ErkJggg==") right repeat-y;
  }

  /* C++ color coding */

  .ace-tm .ace_storage, .ace-tm .ace_keyword {
    color: #3281c6;
  }

  .ace-tm .ace_constant {
    color: #ed2c5d;
  }

  .ace-tm .ace_string {
    color: #1f8000;
  }

  .ace-tm .ace_constant.ace_numeric {
    color: #ac7339;
  }

  /* e.g. printf */
  .ace-tm .ace_support.ace_function {
    color: #66d9ef;
  }

  /***** C Documentation *****/

  /* from corresponding C++ styles */

  /* bool */
  .c.source-c .kw2 {
    color: #84c103;
  }

  /* keyword */
  .c.source-c .kw4 {
    color: #ac7339;
  }

  /* numeric literal */
  .c.source-c .nu0 {
    color: #7aa3c3;
  }

  .c.source-c .nu16, .c.source-c .nu17 {
    color: #6d99c8;
  }

  /* template, conditional, loop keywords */
  .c.source-c .kw1 {
    color: #4fa2b3;
  }

  /* comparison & shift operators */
  .c.source-c .sy1 {
    color: #4b7ea5;
  }

  /* indirection, increment, decrement operators */
  .c.source-c .sy2 {
    color: #4fa2b3;
  }

  /* not, address-of operator */
  .c.source-c .sy3 {
    color: #4fa2b3;
  }

  /***** ja.cppreference.com *****/

  /** Translated Text on hover **/

  /* Highlighted Text */
  .t-tr-text:hover {
    background-color: var(--lighter-bkgd-color);
  }

  /* Pop-up Original Text */
  .t-tr-dropdown > div > div {
    background-color: var(--default-bkgd-color);
    border-color: var(--border-color);
  }

  .t-tr-dropdown-arrow {
    border-bottom-color: var(--default-bkgd-color);
  }

  .t-tr-dropdown-arrow-border {
    border-bottom-color: var(--border-color);
  }

  /** Separators in rows e.g. ja.cppreference.com/w/cpp/string/byte **/
  .t-dsc-sep > td {
    border-top-color: var(--border-color);
  }

}

/* ==UserStyle==
@name           Hacker News - Zenburn Dark Narrow
@namespace      USO Archive
@author         vvlad
@description    `Dark and narrow hacker news style.Colors are from https://userstyles.org/styles/97106/hacker-news-zenburn-dark`
@version        20200502.12.5
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("news.ycombinator.com") {
/* Adapted from https://userstyles.org/styles/97106/hacker-news-zenburn-dark */
* {
    color: #dcdccc !important;
    background-color: #404040 !important;
  }
  body {
    /* max-width: 700px; */
    margin: 0 auto;
  }
  body > center > table > tbody > tr:first-child * {
    background-color: #505050 !important;
  }
  body > center > table > tbody > tr:first-child * a:hover {
    background: #404040 !important;
  }
  code, pre, input, textarea {
    background: #505050 !important;
  }
  a {
    color: #7F9F7F !important;
  }
  .subtext a {
    color: #dcdccc !important;
  }
  a:visited, a:visited span {
    color: #CC9393 !important;
  }
  a:hover, a:hover span {
    background: #505050 !important;
  }
}

/* ==UserStyle==
@name           10.23.5.47 - 12/5/2022, 4:31:08 PM
@namespace      github.com/ian-h-chamberlain/robot
@version        1.0.0
@description    Robot logs dark mode
@author         Ian Chamberlain
==/UserStyle== */
@-moz-document regexp(".*\\/log[^*]+[.]html") {
    /* ripped from https://github.com/robotframework/robotframework/pull/4190 */
    :root {
        --text-color: black;
        --background-color: white;
        --primary-color: #ddd;
        --secondary-color: #ccc;
        --link-color: #15c;
        --link-hover-color: #61c;
        --highlight-color: #f4f4f4;
        --pass-color: #97bd61;
        --fail-color: #ce3e01;
        --warn-color: #fed84f;
        --ascending-icon: url(data:image/gif;base64,R0lGODlhCwAJAKEAAAAAAH9/fwAAAAAAACH5BAEAAAIALAAAAAALAAkAAAIUlBWnFr3cnIr0WQOyBmvzp13CpxQAOw==);
        --descending-icon: url(data:image/gif;base64,R0lGODlhCwAJAKEAAAAAAH9/fwAAAAAAACH5BAEAAAIALAAAAAALAAkAAAIUlAWnBr3cnIr0WROyDmvzp13CpxQAOw==);
    }

    @media (prefers-color-scheme: dark) {
        :root {
            --text-color: white;
            --background-color: #1c2227;
            --primary-color: #26373b;
            --secondary-color: #424f5a;
            --link-color: #52adff;
            --link-hover-color: #bb86fc;
            --highlight-color: #242b31;
            --ascending-icon: url(data:image/gif;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAAJAgMAAACZCj6+AAAAAXNSR0IB2cksfwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAlQTFRFAAAAfn5+////f/cqYgAAAAN0Uk5TAP//RFDWIQAAACdJREFUeJxjYHBgYGAMYGBgDWFgEA1lAAOtVQwMXCsYGJgWADkNDAA78QP9oKr7vwAAAABJRU5ErkJggg==);
            --descending-icon: url(data:image/gif;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAAJAgMAAACZCj6+AAAAAXNSR0IB2cksfwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAlQTFRFAAAA////fn5+K6jOaAAAAAN0Uk5TAP//RFDWIQAAACdJREFUeJxjYHBgYGAMYGBgDWFgEA1lAAOtVQwMXCsYGJgWADkNDAA78QP9oKr7vwAAAABJRU5ErkJggg==);
        }
    }
    /* Generic and misc styles */
    body {
        font-family: Helvetica, sans-serif;
        font-size: 0.8em;
        color: var(--text-color);
        padding: 6px;
        background: var(--background-color);
    }
    table {
        table-layout: fixed;
        word-wrap: break-word;
        empty-cells: show;
        font-size: 1em;
    }
    th,
    td {
        vertical-align: top;
    }
    br {
        mso-data-placement: same-cell;
        /* maintain line breaks in Excel */
    }
    hr {
        background: var(--secondary-color);
        height: 1px;
        border: 0;
    }
    a,
    a:link,
    a:visited {
        text-decoration: none;
        color: var(--link-color);
    }
    a > img {
        border: 1px solid var(--link-color) !important;
    }
    a:hover,
    a:active {
        text-decoration: underline;
        color: var(--link-hover-color);
    }
    select {
        background-color: var(--background-color);
        color: var(--text-color);
        border: 2px solid var(--secondary-color);
        border-radius: 4px;
    }
    .parent-name {
        font-size: 0.7em;
        letter-spacing: -0.07em;
    }
    .message {
        white-space: pre-wrap;
    }
    /* Headers */
    #header {
        width: 65em;
        height: 3em;
        margin: 6px 0;
    }
    h1 {
        float: left;
        margin: 0 0 0.5em 0;
        width: 75%;
    }
    h2 {
        clear: left;
    }
    #generated {
        float: right;
        text-align: right;
        font-size: 0.9em;
        white-space: nowrap;
    }
    /* Documentation headers */
    .doc > h2 {
        font-size: 1.2em;
    }
    .doc > h3 {
        font-size: 1.1em;
    }
    .doc > h4 {
        font-size: 1.0em;
    }
    /* Status text colors -- !important allows using them in links */
    .fail {
        color: var(--fail-color) !important;
        font-weight: bold;
    }
    .pass {
        color: #098a09 !important;
    }
    .skip {
        color: #927201 !important;
        font-weight: bold;
    }
    .label {
        padding: 2px 5px;
        font-size: 0.75em;
        letter-spacing: 1px;
        white-space: nowrap;
        color: var(--text-color);
        background-color: var(--primary-color);
        border-radius: 3px;
    }
    .label.debug,
    .label.trace,
    .label.error,
    .label.keyword {
        letter-spacing: 0;
    }
    .label.pass,
    .label.fail,
    .label.error,
    .label.skip,
    .label.warn {
        font-weight: bold;
    }
    .label.pass {
        background-color: var(--pass-color);
        color: #000 !important;
    }
    .label.fail,
    .label.error {
        background-color: var(--fail-color);
        color: #fff !important;
    }
    .label.skip,
    .label.warn {
        background-color: var(--warn-color);
        color: #000 !important;
    }
    /* Top right header */
    #top-right-header {
        position: fixed;
        top: 0;
        right: 0;
        z-index: 1000;
        width: 12em;
        text-align: center;
    }
    #report-or-log-link a {
        display: block;
        background: black;
        color: white;
        text-decoration: none;
        font-weight: bold;
        letter-spacing: 0.1em;
        padding: 0.3em 0;
        border-bottom-left-radius: 4px;
    }
    #report-or-log-link a:hover {
        color: #ddd;
    }
    #log-level-selector {
        padding: 0.3em 0;
        font-size: 0.9em;
        border-bottom-left-radius: 4px;
        background: var(--primary-color);
    }
    /* Statistics table */
    .statistics {
        width: 65em;
        border-collapse: collapse;
        empty-cells: show;
        margin-bottom: 1em;
    }
    .statistics tr:hover {
        background: var(--highlight-color);
        cursor: pointer;
    }
    .statistics th,
    .statistics td {
        border: 1px solid var(--secondary-color);
        padding: 0.1em 0.3em;
    }
    .statistics th {
        background-color: var(--primary-color);
        padding: 0.2em 0.3em;
    }
    .statistics td {
        vertical-align: middle;
    }
    .stats-col-stat {
        width: 4.5em;
        text-align: center;
    }
    .stats-col-elapsed {
        width: 5.5em;
        text-align: center;
    }
    .stats-col-graph {
        width: 9em;
    }
    th.stats-col-graph:hover {
        cursor: default;
    }
    .stat-name {
        float: left;
    }
    .stat-name a,
    .stat-name span {
        font-weight: bold;
    }
    .tag-links {
        font-size: 0.9em;
        float: right;
        margin-top: 0.05em;
    }
    .tag-links span {
        margin-left: 0.2em;
    }
    /* Statistics graph */
    .graph,
    .empty-graph {
        border: 1px solid var(--secondary-color);
        width: auto;
        height: 7px;
        padding: 0;
        background: #aaa;
    }
    .empty-graph {
        background: var(--highlight-color);
    }
    .pass-bar,
    .fail-bar,
    .skip-bar {
        float: left;
        height: 100%;
    }
    .fail-bar {
        background: var(--fail-color);
    }
    .pass-bar {
        background: var(--pass-color);
    }
    .skip-bar {
        background: var(--warn-color);
    }
    /* Tablesorter - adapted from provided Blue Skin */
    .tablesorter-header {
        background-image: url(data:image/gif;base64,R0lGODlhCwAJAIAAAH9/fwAAACH5BAEAAAEALAAAAAALAAkAAAIRjAOnBr3cnIr0WUjTrC9e9BQAOw==);
        background-repeat: no-repeat;
        background-position: center right;
        cursor: pointer;
    }
    .tablesorter-header:hover {
        background-color: var(--secondary-color);
    }
    .tablesorter-headerAsc {
        background-image: var(--ascending-icon);
        background-color: var(--secondary-color) !important;
    }
    .tablesorter-headerDesc {
        background-image: var(--descending-icon);
        background-color: var(--secondary-color) !important;
    }
    .sorter-false {
        background-image: none;
        cursor: default;
    }
    .sorter-false:hover {
        background-color: var(--primary-color);
    }


    /* Icons are from Open Iconic <https://useiconic.com/open/>.
   Licensed under the MIT License. */
    :root {
        --icon-filter: invert(0);
        --icon-highlight: var(--secondary-color);
        --elapsed-color: #666;
    }

    @media (prefers-color-scheme: dark) {
        :root {
            --icon-filter: invert(1);
            /* Invert colors for the icons */
            --icon-highlight: #a39990;
            /* Dark mode secondary color inverted (--icon-filter will invert it back) */
            --elapsed-color: #999;
        }
    }
    /* Containers */
    .suite,
    .test,
    #errors {
        border: 1px solid var(--secondary-color);
        padding: 0.3em 0.2em;
        margin: 0.2em 0;
    }
    .test {
        border-style: dashed;
    }
    #errors,
    .messages {
        width: 100%;
        border-spacing: 0;
    }
    .children {
        display: none;
        margin-left: 1.4em;
    }
    .suite,
    .test,
    .keyword {
        margin-left: -0.2em;
    }
    #s1,
    .suite > .children > .keyword {
        margin-left: 0;
    }
    /* Suite, test and kw headers */
    .element-header {
        border: 1px solid transparent;
        border-radius: 2px;
        position: relative;
    }
    .element-header:hover {
        cursor: pointer;
        background-color: var(--highlight-color);
        border-color: var(--secondary-color);
    }
    .element-header-toggle {
        position: absolute;
        left: 3px;
        top: 5px;
        background-repeat: no-repeat;
        background-position: center;
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGCAQAAABKxSfDAAAAAmJLR0QA/4ePzL8AAAAJcEhZcwAACxMAAAsTAQCanBgAAAAHdElNRQfeCR0JChSkZz20AAAAGklEQVQI12NgQAKMDKzInP8IDhOqMk4G7AAANQwBE427PYUAAAAASUVORK5CYII=);
        background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI4IiBoZWlnaHQ9IjgiIHZpZXdCb3g9IjAgMCA4IDgiPgogIDxwYXRoIGQ9Ik0wIDB2Mmg4di0yaC04eiIgdHJhbnNmb3JtPSJ0cmFuc2xhdGUoMCAzKSIgLz4KPC9zdmc+), none;
        filter: var(--icon-filter);
        height: 10px;
        width: 10px;
        background-size: 6px 6px;
        border: 1px solid var(--icon-highlight);
        border-radius: 2px;
    }
    .closed > .element-header-toggle {
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGCAQAAABKxSfDAAAAAmJLR0QA/4ePzL8AAAAJcEhZcwAACxMAAAsTAQCanBgAAAAHdElNRQfeCR0JCDHdVYtxAAAAM0lEQVQI103MMQqAMBQFwVHyITaB3P+W1sKzSBC3mW6hRFw0pUB5DvF1bu9FN0RM3X/wAk98CUnvFTakAAAAAElFTkSuQmCC);
        background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI4IiBoZWlnaHQ9IjgiIHZpZXdCb3g9IjAgMCA4IDgiPgogIDxwYXRoIGQ9Ik0zIDB2M2gtM3YyaDN2M2gydi0zaDN2LTJoLTN2LTNoLTJ6IiAvPgo8L3N2Zz4=), none;
    }
    .element-header:hover > .element-header-toggle {
        background-color: var(--icon-highlight);
    }
    .element-header-right:hover ~ .element-header-toggle {
        background-color: transparent;
    }
    .element-header-left {
        padding: 3px 80px 3px 20px;
    }
    .element-header-right {
        position: absolute;
        right: 0;
        top: 0;
        padding: 3px;
        cursor: default;
    }
    .element-header .label {
        margin-right: 0.5em;
    }
    .name {
        font-weight: bold;
    }
    .elapsed {
        float: right;
        color: var(--elapsed-color);
        padding-left: 1em;
    }
    .link {
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAYAAADED76LAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEwAACxMBAJqcGAAAAHlJREFUGJVtzrEJwmAQBtCn6ASuIKTIBhY2gmghBiJYB1udwk10B8FJbKMBcQFbtbkfJHrVwfu+4/idJR64Y9H7E3higj5O3S8Y44ApLnjhnXCNGhV2yNGggDJwiC02gavUbpDFvscRo4QdXOMKDHBufz3HLYKzNn4AbSkV3fncGQYAAAAASUVORK5CYII=);
        background-image: url(data:image/svg+xml;base64,), none;
        filter: var(--icon-filter);
    }
    .expand {
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAYAAADED76LAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEwAACxMBAJqcGAAAADtJREFUGJVjYCACKDAwMByA0ljFDjAwMPxnYGB4ABVQgLL/Q+VQBB6gseGmIivCkERXhFUSWRFOSawAAEl7E3uv1iMcAAAAAElFTkSuQmCC);
        background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI4IiBoZWlnaHQ9IjgiIHZpZXdCb3g9IjAgMCA4IDgiPgogIDxwYXRoIGQ9Ik0xLjUgMGwtMS41IDEuNSA0IDQgNC00LTEuNS0xLjUtMi41IDIuNS0yLjUtMi41eiIgdHJhbnNmb3JtPSJ0cmFuc2xhdGUoMCAxKSIgLz4KPC9zdmc+), none;
        filter: var(--icon-filter);
    }
    .collapse {
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAYAAADED76LAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEwAACxMBAJqcGAAAAC5JREFUGJVjYCARNEAxTsn/UIyhCFkSQxG6ThQ+C5IpjWhG1yNb4YDFTdjEMAEACi8Q++9Y9TAAAAAASUVORK5CYII=);
        background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI4IiBoZWlnaHQ9IjgiIHZpZXdCb3g9IjAgMCA4IDgiPgogIDxwYXRoIGQ9Ik00IDBsLTQgNCAxLjUgMS41IDIuNS0yLjUgMi41IDIuNSAxLjUtMS41LTQtNHoiIHRyYW5zZm9ybT0idHJhbnNsYXRlKDAgMSkiIC8+Cjwvc3ZnPg==), none;
        filter: var(--icon-filter);
    }
    .expand,
    .collapse,
    .link {
        float: left;
        display: block;
        visibility: hidden;
        margin: 0 4px;
        height: 14px;
        width: 14px;
        background-size: 8px 8px;
        background-repeat: no-repeat;
        background-position: center;
        border: 1px solid var(--icon-highlight);
        border-radius: 2px;
    }
    .link {
        background-size: 10px 10px;
    }
    .element-header:hover a {
        visibility: visible;
    }
    .expand:hover,
    .collapse:hover,
    .link:hover {
        background-color: var(--icon-highlight);
    }
    /* Messages and errors */
    .messages .time,
    .messages .message {
        font-family: monospace;
        font-size: 1.1em;
    }
    #errors .message {
        font-family: monospace;
        font-size: 1.2em;
    }
    .message-row {
        height: 20px;
    }
    .time {
        width: 7.5em;
    }
    .error-time {
        width: 11em;
        font-size: 0.9em;
        white-space: nowrap;
    }
    .level {
        width: 5em;
        text-align: center;
    }
    .select-message {
        width: 24px;
    }
    .select-message > div {
        float: right;
        margin-right: 2px;
        height: 16px;
        width: 16px;
        background-size: 12px 12px;
        background-repeat: no-repeat;
        background-position: center;
    }
    .message-row:hover .select-message div {
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAQAAAD8fJRsAAAAAmJLR0QA/4ePzL8AAAAJcEhZcwAACxMAAAsTAQCanBgAAAAHdElNRQfeCR0MOzhWKZQOAAAASUlEQVQY032PsRHAIAwDX9BkicD+W8UMEqegAQJWKZ/+3sLZJhFGQEZzNaIa4ibh66En867VNaIeoP5WppNuIGoAlOm3g5VC1AdYeQv1BZPRSgAAAABJRU5ErkJggg==);
        background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI4IiBoZWlnaHQ9IjgiIHZpZXdCb3g9IjAgMCA4IDgiPgogIDxwYXRoIGQ9Ik0wIDB2MWg4di0xaC04em0wIDJ2MWg2di0xaC02em0wIDJ2MWg4di0xaC04em0wIDJ2MWg2di0xaC02eiIgLz4KPC9zdmc+), none;
        filter: var(--icon-filter);
        border: 1px solid var(--icon-highlight);
        border-radius: 2px;
    }
    .select-message:hover div {
        background-color: var(--icon-highlight);
        cursor: pointer;
    }
    /* Message tables - these MUST NOT be combined together because otherwise
   dynamically altering them based on visible log level is not possible. */
    .trace-message {
        display: table;
    }
    .debug-message {
        display: table;
    }
    /* Metadata */
    .metadata {
        width: 100%;
        border-spacing: 0.2em;
    }
    .metadata th {
        width: 12em;
        vertical-align: top;
        text-align: left;
    }
    .metadata td {
        vertical-align: top;
    }
    .keyword-metadata {
        font-size: 0.9em;
    }
    /* Custom styles for statistics */
    #total-stats tr:hover,
    #tag-stats tr:hover {
        cursor: default;
    }
}

/* ==UserStyle==
@name           Darklingo++ - [UPDATED]
@namespace      USO Archive
@author         Henry Sanger
@description    `BEFORE YOU INSTALL - I would strongly recommend using <a href="https://github.com/openstyles/stylus/#readme">Stylus</a> instead of Stylish. Stylish is <a href="https://robertheaton.com/2018/07/02/stylish-browser-extension-steals-your-internet-history/">spyware which steals your browsing history.</a> Also, the Stylus version of this style is updated more regularly.To install this style with Stylus, visit userstyles.world. <a href="https://userstyles.world/style/4605/darklingo-updated">Link to this style on userstyles.world</a>An updated version of Darklingo++, a dark theme for Duolingo, originally created by <a href="https://userstyles.org/users/709142">Justin Ikeako</a>.<a href="https://userstyles.org/styles/169205/darklingo">Link to the original style</a>`
