@advanced dropdown scroll "Scroll Bar" {
	on "On*" <<<EOT /* Scrollbar *\/
html {
  scrollbar-color: #fff4 var(--50)
}

._2jbOb ._2-Lx6,
._3P4YK {
  scrollbar-width: thin
}

::-webkit-scrollbar {
	display: block;
	background: var(--50);
	background-image: url(data:image/svg+xml;base64,), url(data:image/svg+xml;base64,);
	background-position: top center, bottom center;
	background-size: contain;
	background-repeat: no-repeat
}


/* Track *\/
::-webkit-scrollbar-track {
	background: var(--50);
	border: none
}


/* Thumb *\/
::-webkit-scrollbar-thumb {
	background: #ffffff40 content-box;
	border: 2px solid transparent
}

::-webkit-scrollbar-thumb:hover {
	background-color: #fff5
}

::-webkit-scrollbar-thumb:active {
	background-color: #fff6
}

/* Buttons *\/
::-webkit-scrollbar-button:single-button {
  display: block;
  height: 16px;
  width: 16px;
	background: var(--50-overlay-heavy)
}

::-webkit-scrollbar-button:single-button:hover {
	background: var(--50-overlay-medium)
} EOT;
off "Off*" <<<EOT /* Scrollbar *\/
* {
  scrollbar-width: none
}

::-webkit-scrollbar {
	display: none
} EOT;

}
@advanced dropdown hideCharacters "Hide Characters" {
	off "Off*" <<<EOT  EOT;
on "On*" <<<EOT div[data-test="session-duo"] ._3a8EI,
