@-moz-document domain(www.wanikani.com) {
  .character-grid__items > li .character-item--radical, .subject-legend ul span.subject-legend__item-badge--all, .sitemap__expandable-chunk[class*="radical"], .popover.srs .popover-content li:nth-child(1), .radical-icon, [class*="highlight-radical"],
  [class*="radical-highlight"], .kotoba-table-list table [class^="radical"], .subject-character.subject-character--radical span.subject-character__characters, .components-list a[class$=radical], #last-items li a .last-item__characters--radical, #lessons .ui ul item.radical,
