/* ==UserStyle==
@name           Custom LeetCode Editor Font
@version        20220712.03.07
@namespace      userstyles.world/user/lance1o7
@description    This script will change the code editor font as configured (default "JetBrains Mono NL").
@author         lance1o7
@license        No License
@advanced text  leetcode-font-family "Editor font" "JetBrains Mono NL"
==/UserStyle== */

@-moz-document domain("leetcode.com") {
.container__YPDh .CodeMirror {
    position: absolute;
    height: 100%;
    width: 100%;
    line-height: normal;
    font-family: var(--leetcode-font-family), 'Monaco', 'Menlo', 'Ubuntu Mono', 'Consolas', 'source-code-pro', monospace;
    padding: 0;
    border-radius: 0;
    border: 0;
}
}

/* ==UserStyle==
@name         GitHub Custom Fonts
@namespace    https://github.com/ian-h-chamberlain
@version      1.0.0
@description  Customize the font used for code on GitHub. Forked from https://github.com/StylishThemes/GitHub-Dark.
@homepageURL  https://gist.github.com/ian-h-chamberlain/3e3a862f0b95e2429839231080fde7bd
@updateURL    https://gist.github.com/ian-h-chamberlain/3e3a862f0b95e2429839231080fde7bd/raw/github-custom-fonts.user.css
@license      BSD-2-Clause
@author       ian-h-chamberlain
@preprocessor default
@var text ghd-font-family "Code font" Menlo
@var text ghd-font-feature-settings "Code font feature Settings" normal
@var text ghd-font-variant-ligatures "Code font ligature Settings" normal
@var text ghd-font-size "Code font size" 12px
==/UserStyle== */
@-moz-document regexp("^https?://((education|gist|graphql|guides|raw|resources|status|developer|support|vscode-auth)\\.)?github\\.com/((?!generated_pages/preview).)*$"), regexp("^https?://www\.zuora\.com.*github\.com.*"), domain("githubusercontent.com"), domain("www.githubstatus.com"), domain("stylishthemes.github.io") {
  pre, code, tt, kbd:not(.badmono), samp, .blob-code, .file-data pre, .line-data,
  #gist-form .file .input textarea, .blob-code-inner, .react-code-text, .jujkut,
  #read-only-cursor-text-area, .react-blob-print-hide {
    font-family: var(--ghd-font-family), Consolas, "Liberation Mono", Menlo, Courier, monospace !important;
    font-feature-settings: var(--ghd-font-feature-settings);
    font-variant-ligatures: var(--ghd-font-variant-ligatures);
    font-size: var(--ghd-font-size) !important;
  }
}

/* ==UserStyle==
@name           Jenkins UI Tweaks
@namespace      github.com/ian-h-chamberlain
@version        1.0.0
@description    Make Replay button less confusing and highlight failure steps.
@updateURL      https://raw.githubusercontent.com/ian-h-chamberlain/dotfiles/main/.config/stylus/styles/jenkins-ui.user.css
@author         Ian Chamberlain @ian-h-chamberlain
==/UserStyle== */
@-moz-document domain("jenkins.lab.128technology.com"), domain("jenkins.ssn.juniper.net") {
    /* Rename the "Rebuild" as "Replay" if that's what it does */
    [href$="/replay"] .task-link-text {
        text-decoration: line-through !important;
        color: var(--text-color-secondary) !important;
    }
    [href$="/replay"] .task-link-text::after {
        content: "Replay" !important;
        text-decoration: none;
        display: inline-block;
        padding-left: 1ex;
        color: var(--text-color) !important;
    }

    code, pre {
        font-size: 12px;
        /*
        This is weird, 300 seems to be heavier than 400 when using Argon Var.
        Non-Var seems okay though.
        */
        font-weight: 500;
    }
}

@-moz-document regexp("https://jenkins[.](lab[.]128technology[.]com|ssn[.]juniper[.]net)/.*/flowGraphTable(/.*)?") {
    /*
    Highlight failed pipeline steps with a different background color
    For Firefox, requires `layout.css.has-selector.enabled` in about:config */
    tr:has(td .icon-red:not([title="failed"])),
    tr:has(td .icon-red) td {
        background-color: rgba(255, 0, 0, 0.3);
    }
    tr:has(td .icon-blue-anime),
    tr:has(td .icon-blue-anime) td { 
        background-color: rgba(0, 0, 255, 0.2); 
    }
}
