sections.code:
:root {
    --text-1: #ddd;
    --text-2: #bbb;
    --text-3: #555;
    --text-blue: #59d;
    --text-green: #5d9;
    --text-orange: #b50;
    --text-red: #b00;
    --bg-body: #222;
    --bg-dark-grey: #333;
    --bg-grey: #444;
    --bg-light-grey: #666;
    --bg-blue: #337;
    --bg-yellow: #663;
    --bg-bright-yellow: #b92;
    --bg-green: #373;
    --bg-red: #733;
    --border-colour: #555;
    --border: 1px solid var(--border-colour);
  }

  /* Root elements */
  body,
  tbody .homepage-section,
  body.aui-page-focused,
  body.aui-page-notification,
  body, body.aui-page-fixed,
  body.aui-page-hybrid,
  body.aui-page-focused #footer-logo,
  body.aui-page-notification #footer-logo,
  body #footer-logo,
  body.aui-page-fixed #footer-logo,
  body.aui-page-hybrid #footer-logo,
  .homepage-footer,
  .homepage-teams,
  #content .aui-sidebar + .aui-page-header,
  table.aui > tbody > tr,
  .aui-page-panel-content,
  table.bb-list tr:hover .labels,
  table.bb-list tr .labels,
  .file-tree-wrapper,
  .pull-request-header .reviewer-avatar-list {
    color: var(--text-1);
    background-color: var(--bg-body);
  }

  a,
  .aui-nav-vertical>li>a,
  .aui-navgroup-vertical .aui-nav>li>a,
  .aui-dialog2-header-main,
  .aui-dialog2-header-close,
  .aui-dialog2-footer-hint {
    color: var(--text-blue);
  }

  .aui-sidebar-submenu .aui-navgroup-vertical .aui-nav .aui-nav-selected>.aui-nav-item,
  .aui-sidebar .aui-navgroup-vertical .aui-nav .aui-nav-selected>.aui-nav-item {
    color: #9df;
  }

  input {
    background-color: var(--bg-dark-grey);
  }

  .stash-text {
    color: var(--text-2);
    border-color: var(--border-colour);
    box-shadow: inset 0 1px 3px var(--border-colour);
  }

  #announcement-banner > div {
    background-color: var(--bg-dark-grey) !important;
    border-color: var(--bg-red) !important;
  }


  table.aui .aui-icon.build-icon.inprogress-build-icon,
  .aui-icon.build-icon.inprogress-build-icon {
    color: var(--text-blue) !important;
  }

  table.aui .aui-icon.build-icon.successful-build-icon,
  .aui-icon.build-icon.successful-build-icon {
    color: var(--text-green) !important;
  }

  table.aui .aui-icon.build-icon.failed-build-icon,
  .aui-icon.build-icon.failed-build-icon {
    color: var(--text-red) !important;
  }

  /* Main page panel */
  .aui-page-panel {
    color: var(--text-1);
    background-color: var(--bg-body);
    border-color: var(--border-colour);
  }

  /* Repositories menu */
  .aui-dropdown2-heading,
  .aui-dropdown2.aui-style-default .aui-dropdown2-section>strong,
  .aui-dropdown2.aui-style-default>strong {
    color: var(--text-1);
  }

  .bitbucket-theme .recent-repositories-menu .public-repo-list-link-section .public-repos-link.icon-repository,
  .bitbucket-theme .recent-repositories-menu .public-repo-list-link-section .public-repos-link.icon-repository:hover,
  .bitbucket-theme .recent-repositories-menu.aui-dropdown2 ul.repository-list li a,
  .bitbucket-theme .recent-repositories-menu.aui-dropdown2 ul.repository-list li a:focus,
  .bitbucket-theme .recent-repositories-menu.aui-dropdown2 ul.repository-list li a:hover {
    color: var(--text-blue);
  }

  .repository-list-container .search-results-container .search-results-item a > .item-wrapper > .item-description {
    color: var(--text-2);
  }

  .repository-list.search-results-container img {
    background-color: var(--bg-light-grey);
  }

  /* Sidebar */
  .aui-sidebar .aui-sidebar-wrapper,
  .aui-sidebar-submenu .aui-sidebar-wrapper {
    background-color: var(--bg-dark-grey);
    border-color: var(--border-colour);
  }

  .aui-sidebar .aui-sidebar-footer > .aui-sidebar-settings-button:hover,
  .aui-sidebar-submenu .aui-sidebar-footer > .aui-sidebar-settings-button:hover,
  .aui-sidebar .aui-sidebar-footer > .aui-sidebar-toggle:hover,
  .aui-sidebar-submenu .aui-sidebar-footer > .aui-sidebar-toggle:hover,
  .aui-sidebar .aui-sidebar-footer > .aui-sidebar-settings-button:focus,
  .aui-sidebar-submenu .aui-sidebar-footer > .aui-sidebar-settings-button:focus,
  .aui-sidebar .aui-sidebar-footer > .aui-sidebar-toggle:focus,
  .aui-sidebar-submenu .aui-sidebar-footer > .aui-sidebar-toggle:focus {
    background-color: var(--border-colour);
  }

  .aui-sidebar-submenu .aui-nav>li>.aui-nav-item>.aui-icon,
  .aui-sidebar .aui-nav>li>.aui-nav-item>.aui-icon {
    filter: brightness(1.6);
  }

  /* Search box */
  #quick-search-dropdown {
    background-color: var(--bg-dark-grey);
    border-color: var(--border-colour);
  }

  .quick-search-dropdown-content h2 {
    color: var(--text-2);
  }

  #quick-search-dropdown li.search-for-code {
    background-color: var(--bg-grey);
  }

  #quick-search-dropdown li.search-for-code>a {
    color: var(--text-1);
  }

  #quick-search-dropdown li.selected,
  #quick-search-dropdown li.search-for-code.selected {
    background-color: var(--bg-blue);
  }

  /* Section dividers */
  .aui-navgroup-vertical .aui-nav,
  .aui-navgroup-vertical .aui-nav-heading,
  .aui-sidebar .aui-sidebar-footer,
  .aui-sidebar-submenu .aui-sidebar-footer,
  .pull-request-activity .rescope .commits-table tbody > tr:first-child {
    border-color: var(--border-colour);
  }

  li.has-actions:hover,
  .aui-sidebar button.aui-nav-item.aui-button.aui-button-link:hover {
    color: #3572b0;
    background-color: var(--bg-body);
  }

  /* Alerts and messages */
  .aui-message {
    color: var(--text-1);
    background: var(--bg-grey);
    border-color: var(--bg-blue);
  }

  .aui-message:before {
    background-color: var(--bg-blue);
  }

  .aui-message.hint, .aui-message-hint,
  .aui-message.info, .aui-message-info {
    color: var(--text-1);
    background: var(--bg-grey);
    border-color: var(--bg-blue);
  }

  .aui-message.hint:before, .aui-message-hint:before,
  .aui-message.info:before, .aui-message-info:before {
    background-color: var(--bg-blue);
  }

  .aui-message.warning, .aui-message-warning {
    color: var(--text-1);
    background: var(--bg-grey);
    border-color: var(--bg-bright-yellow);
  }

  .aui-message.warning:before, .aui-message-warning:before {
    background-color: var(--bg-bright-yellow);
  }

  .aui-message.warning:after, .aui-message-warning:after {
    color: white;
  }

  .aui-message.error, .aui-message-error {
    color: var(--text-1);
    background: var(--bg-grey);
